class ContextualNotes{static hideOnScroll=true;static showEndnotesOnClose=false;static endnotes;static observer;constructor(){ContextualNotes.disableHideOnScroll(2);var note=ContextualNotes.currentNote;if(note){note.opacity=0;window.setTimeout(function(){ContextualNotes.showNote();note.opacity=1;},500);}
window.addEventListener('hashchange',function(){ContextualNotes.showNote();},false);window.addEventListener('scroll',function(){if(location.hash.startsWith('#fn:')&&ContextualNotes.hideOnScroll){ContextualNotes.closeNote();}});window.addEventListener('touchend',function(event){if(event.target.classList.contains('footnote-ref')){disableHideOnScroll(0.5);event.preventDefault();event.stopPropagation();event.target.click();}});ContextualNotes.observer=new IntersectionObserver(ContextualNotes.endnoteIntersectionCallback);ContextualNotes.endnotes=document.querySelector('.footnotes');if(ContextualNotes.endnotes){ContextualNotes.observer.observe(ContextualNotes.endnotes);}}
static disableHideOnScroll(seconds){ContextualNotes.hideOnScroll=false;window.setTimeout(function(){ContextualNotes.hideOnScroll=true;},seconds*1000);}
static get currentNote(){if(location.hash.startsWith('#fn:')){return document.getElementById(location.hash.slice(1));}}
static showNote(){var note=ContextualNotes.currentNote;if(note){if(ContextualNotes.endnotes.classList.contains('endnotes')){ContextualNotes.endnotes.classList.remove('endnotes');ContextualNotes.showEndnotesOnClose=true;ContextualNotes.disableHideOnScroll(2);}
ContextualNotes.positionContextualNote(note);var backref=note.getElementsByClassName('footnote-backref').item(0);backref.addEventListener('click',ContextualNotes.onNoteClose);}}
static closeNote(note){note=note||ContextualNotes.currentNote;if(note){location.hash='#-';var backref=note.getElementsByClassName('footnote-backref').item(0);backref.removeEventListener('click',ContextualNotes.onNoteClose);if(ContextualNotes.showEndnotesOnClose){ContextualNotes.showEndnotes()
ContextualNotes.showEndnotesOnClose=false;}}}
static onNoteClose(event){var note=this.parentNode;if(note.localName!='li'){note=note.parentNode;}
if(location.hash=='#'+note.getAttribute('id')){event.preventDefault();event.stopPropagation();}
ContextualNotes.closeNote(note);}
static positionContextualNote(note){let mql=window.matchMedia('(min-width: 415px)');if(!mql.matches){return;}
var backref=note.getElementsByClassName('footnote-backref').item(0);var ref=document.getElementById(backref.getAttribute('href').slice(1));var refLocation=ref.getBoundingClientRect();var noteTop=refLocation.top+refLocation.height+5+'px';note.style.top=refLocation.top+refLocation.height+5+'px';var noteLeft;if(refLocation.left<(note.clientWidth-125)){noteLeft=Math.max(0,refLocation.left+refLocation.width/2-70);note.classList.add('flip');}else{noteLeft=Math.max(0,Math.min(refLocation.left+refLocation.width/2-note.clientWidth+70,window.innerWidth-note.clientWidth));note.classList.remove('flip');}
note.style.left=noteLeft+'px'
note.style.bottom='auto';var noteLocation=note.getBoundingClientRect();if(noteLocation.bottom>document.documentElement.clientHeight){this.disableHideOnScroll(1);var adjustment=noteLocation.bottom-document.documentElement.clientHeight+10;window.scrollBy(0,adjustment);note.style.top=noteLocation.top-adjustment+'px';}}
static showEndnotes(elem){elem=elem||ContextualNotes.endnotes;elem.classList.add('endnotes');var notes=elem.getElementsByTagName('li');Array.prototype.forEach.call(notes,function(li){li.removeAttribute('style');li.classList.remove('flip');});}
static hideEndnotes(elem){elem=elem||ContextualNotes.endnotes;elem.classList.remove('endnotes');}
static endnoteIntersectionCallback(entries,observer){entries.forEach(entry=>{var elem=entry.target;if(entry.isIntersecting){ContextualNotes.showEndnotes(elem);}else{ContextualNotes.hideEndnotes(elem);}})}}
